import React, {useState} from 'react'
import './Auth.css'
import icon from '../../'

const Auth = () => {

  const {isSignup, setIsSignup} = useState(false)

  return (
    <section class='auth-section'>
      <div class='auth-container'>
        { !isSignup && <img src={icon} alt='stack overflow' className='login-logo' /> }
      </div>
    </section>
  )
}

export default Auth
